import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './AdminStyle.css';
import { Button } from "@mui/material";

const AdminPage = () => {
    const navigate = useNavigate();
    const [clients, setClients] = useState([]);
    const [newUser, setNewUser] = useState({ username: '', password: '', role: 'client', name: '', email: '' });
    const [error, setError] = useState('');
    const [expandedClientId, setExpandedClientId] = useState(null);
    const [selectedUserId, setSelectedUserId] = useState(null);
    const [showDeviceForm, setShowDeviceForm] = useState({});
    const [message, setMessage] = useState('');
    const [updatedUser, setUpdatedUser] = useState({ name: '', email: '', username: '', role: '', password: '' });
    const [showUpdateForm, setShowUpdateForm] = useState({});
    const [newDevice, setNewDevice] = useState({ address: '', consumption: '', description: '' });
    const [updateDeviceId, setUpdateDeviceId] = useState(null);
    const [updateDevice, setUpdateDevice] = useState({ address: '', consumption: '', description: '' });

    const [devices, setDevices] = useState([]); // Stare nouă pentru dispozitivele unui utilizator
    const [clientDevices, setClientDevices] = useState({});

    useEffect(() => {
        const userRole = localStorage.getItem('role');
        if (userRole !== 'admin') {
            alert('You do not have access to this page!');
            navigate('/client');
        } else {
            fetchClients();
        }
    }, [navigate]);

    const handleDeleteDevice = async (deviceId) => {
        if (window.confirm("Are you sure you want to delete this device?")) {
            try {
                await axios.delete(`http://localhost:8083/api/user_device/device/${deviceId}`);
                // Refresh the devices for the client after deletion
                setClientDevices((prevDevices) => {
                    const updatedDevices = { ...prevDevices };
                    // Identifică clientul asociat dispozitivului
                    const clientId = Object.keys(updatedDevices).find(clientId =>
                        updatedDevices[clientId].some(device => device.id === deviceId)
                    );
                    if (clientId) {
                        // Elimină dispozitivul din lista clientului
                        updatedDevices[clientId] = updatedDevices[clientId].filter(device => device.id !== deviceId);
                    }
                    return updatedDevices;
                });            } catch (error) {
                console.error('Error deleting device:', error);
                setMessage('Failed to delete device.');
                setTimeout(() => {
                    setMessage('');
                }, 3000);
            }
        }
    };
    const fetchDevicesForClient = async (clientId) => {
        try {
            const response = await axios.get(`http://localhost:8083/api/user_device/${clientId}/owned_devices`);
            setClientDevices((prevState) => ({
                ...prevState,
                [clientId]: response.data
            }));
        } catch (error) {
            console.error('Error fetching devices:', error);
        }
    };

    const toggleExpandClient = (clientId) => {
        if (expandedClientId === clientId) {
            setExpandedClientId(null);
        } else {
            setExpandedClientId(clientId);
            fetchDevicesForClient(clientId);
        }
    };



    const fetchClients = async () => {
        try {
            const response = await axios.get('http://localhost:8084/api/users');
            setClients(response.data);
        } catch (err) {
            console.error(err);
            setMessage('Failed to fetch clients');
        }
    };
    const handleSignOut = () => {
        localStorage.removeItem('role');
        localStorage.removeItem('userId');
        navigate('/'); // Redirecționează către pagina de login
    }

    const handleUpdateUser = async (e) => {
        e.preventDefault();

        // Loghează datele utilizatorului care urmează să fie actualizat
        console.log("Updating user with data:", updatedUser);

        const updatedUserData = {
            ...updatedUser,
            password: updatedUser.password || '',
        };

        try {
            const response = await fetch(`http://localhost:8084/api/users/${selectedUserId}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(updatedUserData),
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || 'Failed to update user');
            }

            // Resetează formularul după actualizare
            setUpdatedUser({ name: '', email: '', username: '', password: '', role: 'client' });

            const responseData = await response.json();
            console.log(responseData);
            setMessage('User updated successfully!');
            setTimeout(() => {
                setMessage('');
            }, 3000);

            fetchClients();
        } catch (error) {
            console.error('Error updating user:', error);
            setMessage(`Failed to update user: ${error.message}`);
            setTimeout(() => {
                setMessage('');
            }, 3000);
        }
    };
    const handleDeleteUser = async (id) => {
        if (window.confirm("Are you sure you want to delete this user?")) {
            try {
                await axios.delete(`http://localhost:8084/api/users/${id}`);
                fetchClients(); // Reîncarcă lista de utilizatori
            } catch (error) {
                console.error('Error deleting user:', error);
                setError('Failed to delete user');
            }
        }
    };

    const toggleUpdateForm = (e, userId, client) => {
        e.stopPropagation();
        setSelectedUserId(userId); // setează utilizatorul selectat

        if (!client) {
            console.error("Client is undefined");
            return; // oprește execuția dacă client este undefined
        }

        if (showUpdateForm[userId]) {
            // Dacă formularul este ascuns, resetează câmpurile
            setUpdatedUser({
                name: '',
                email: '',
                username: '',
                password: '',
                role: 'client',
            });
        } else {
            // Când formularul de actualizare este deschis, inițializează-l cu datele utilizatorului
            setUpdatedUser({
                name: client.name || '',
                email: client.email || '',
                username: client.username || '',
                role: client.role || 'client',
            });
        }

        setShowUpdateForm((prevState) => ({
            ...prevState,
            [userId]: !prevState[userId], // toggle pentru formularul utilizatorului curent
        }));
    };
    const toggleDeviceForm = (e, userId) => {
        e.stopPropagation(); // Oprește propagarea evenimentului de click
        setSelectedUserId(userId); // Setează userId pentru utilizatorul curent
        setShowDeviceForm((prev) => ({ ...prev, [userId]: !prev[userId] }));
    };

    const handleAddDevice = async (e, userId) => {
        e.preventDefault(); // Previne reîncărcarea paginii
        try {
            const response = await axios.post('http://localhost:8083/api/user_device/insert', {
                ...newDevice,
                userId: userId // Aici folosești userId-ul clientului selectat
            });
            // Setează mesajul de succes
            setMessage('Device added successfully!');

            // Resetează formularul
            setNewDevice({ address: '', consumption: '', description: '' });

            // Actualizează lista de dispozitive pentru clientul adăugat
            fetchDevicesForClient(userId); // Apelează funcția pentru a obține lista actualizată de dispozitive

            // Oprește afișarea mesajului după câteva secunde
            setTimeout(() => {
                setMessage('');
            }, 3000); // După 3 secunde
        } catch (error) {
            console.error('Error adding device:', error);

            // Setează mesajul de eroare
            setMessage('Failed to add device. Please try again.');

            // Oprește afișarea mesajului după câteva secunde
            setTimeout(() => {
                setMessage('');
            }, 3000); // După 3 secunde
        }
    };

    const handleAddUser = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://localhost:8084/api/users/insert', newUser); // API pentru a adăuga utilizatori
            setNewUser({
                name:'',
                email:'',
                username:'',
                password:'',
                role:'client'
            });
            fetchClients(); // Reîncarcă lista după adăugare
            setNewUser({ name:'',email:'',username: '', password: '', role: 'client' }); // Resetează formularul
        } catch (err) {
            console.error(err);
            setError('Failed to add user');
        }
    };
    return (
        <div className="admin-container">
            <h2>Admin Dashboard</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <Button variant="contained" color="secondary" onClick={handleSignOut} style={{ marginBottom: '20px' }}>
                Sign Out
            </Button>

            {/* Secțiunea 1: Lista Clienților */}
            <div className="section">
                <h3>Client List</h3>
                {clients.length > 0 ? (
                    <ul>
                        {clients.map(client => (
                            <li key={client.id} style={{ cursor: 'pointer' }}>
                                {/* Doar titlul utilizatorului este clickabil pentru expand/collapse */}
                                <div onClick={() => toggleExpandClient(client.id)}>
                                    {client.name}
                                </div>

                                {expandedClientId === client.id && (
                                    <div className="client-details">
                                        <div className="client-card">
                                            <p>Id: {client.id}</p>
                                        </div>
                                        <div className="client-card">
                                            <p>Username: {client.username}</p>
                                        </div>
                                        <div className="client-card">
                                            <p>Email: {client.email}</p>
                                        </div>

                                        <button
                                            onClick={(e) => {
                                                e.stopPropagation();
                                                handleDeleteUser(client.id);
                                            }}
                                            className="delete-button button-spacing-"
                                        >
                                            Delete
                                        </button>

                                        <button onClick={(e) => toggleUpdateForm(e, client.id, client)}
                                                className="update-button button-spacing">
                                            {showUpdateForm[client.id] ? 'Hide Update Form' : 'Update'}
                                        </button>

                                        {/* Buton pentru a adăuga un dispozitiv */}
                                        <button
                                            onClick={(e) => toggleDeviceForm(e, client.id)}
                                            className="toggle-device-button button-spacing"
                                        >
                                            {showDeviceForm[client.id] ? 'Hide Device Form' : 'Add Device'}
                                        </button>

                                        {showUpdateForm[client.id] && (
                                            <form onSubmit={handleUpdateUser}>
                                                <input
                                                    type="text"
                                                    placeholder="Name"
                                                    value={updatedUser.name}
                                                    onChange={(e) => setUpdatedUser({
                                                        ...updatedUser,
                                                        name: e.target.value
                                                    })}
                                                    required
                                                />
                                                <input
                                                    type="email"
                                                    placeholder="Email"
                                                    value={updatedUser.email}
                                                    onChange={(e) => setUpdatedUser({
                                                        ...updatedUser,
                                                        email: e.target.value
                                                    })}
                                                    required
                                                />
                                                <input
                                                    type="text"
                                                    placeholder="Username"
                                                    value={updatedUser.username}
                                                    onChange={(e) => setUpdatedUser({
                                                        ...updatedUser,
                                                        username: e.target.value
                                                    })}
                                                    required
                                                />
                                                <select
                                                    value={updatedUser.role}
                                                    onChange={(e) => setUpdatedUser({
                                                        ...updatedUser,
                                                        role: e.target.value
                                                    })}
                                                >
                                                    <option value="client">Client</option>
                                                    <option value="admin">Admin</option>
                                                </select>
                                                <button type="submit">Update</button>
                                            </form>
                                        )}

                                        {/* Formular pentru adăugarea unui dispozitiv */}
                                        {showDeviceForm[client.id] && (
                                            <>
                                                <form onSubmit={(e) => handleAddDevice(e, client.id)}>
                                                    <input
                                                        type="text"
                                                        placeholder="Address"
                                                        value={newDevice.address}
                                                        onChange={(e) => setNewDevice({
                                                            ...newDevice,
                                                            address: e.target.value
                                                        })}
                                                        required
                                                    />
                                                    <input
                                                        type="number"
                                                        placeholder="Consumption"
                                                        value={newDevice.consumption}
                                                        onChange={(e) => setNewDevice({
                                                            ...newDevice,
                                                            consumption: e.target.value
                                                        })}
                                                        required
                                                    />
                                                    <input
                                                        type="text"
                                                        placeholder="Description"
                                                        value={newDevice.description}
                                                        onChange={(e) => setNewDevice({
                                                            ...newDevice,
                                                            description: e.target.value
                                                        })}
                                                        required
                                                    />
                                                    <button type="submit">Add Device</button>
                                                </form>
                                                {message && <p>{message}</p>}
                                            </>
                                        )}

                                        {/* Tabelul cu dispozitivele clientului */}
                                        {clientDevices[client.id] && clientDevices[client.id].length > 0 ? (
                                            <table className="device-table">
                                                <thead>
                                                <tr>
                                                    <th>Address</th>
                                                    <th>Consumption</th>
                                                    <th>Description</th>
                                                    <th>Actions</th> {/* New header for actions */}
                                                </tr>
                                                </thead>
                                                <tbody>
                                                {clientDevices[client.id].map((device) => (
                                                    <tr key={device.id}>
                                                        <td>{device.address}</td>
                                                        <td>{device.consumption}</td>
                                                        <td>{device.description}</td>
                                                        <td>
                                                            <button
                                                                onClick={() => handleDeleteDevice(device.id)}
                                                                className="delete-device-button"
                                                            >
                                                                Delete
                                                            </button>
                                                            <button onClick={() => {
                                                                setUpdateDeviceId(device.id);
                                                                setUpdateDevice(device); // Setează starea pentru formularul de actualizare
                                                                setShowUpdateForm(true); // Deschide formularul de actualizare
                                                            }}>
                                                                Update
                                                            </button>
                                                        </td>
                                                        {/* Button for deleting device */}
                                                    </tr>
                                                ))}
                                                </tbody>
                                            </table>
                                        ) : (
                                            <p>No devices found for this client.</p>
                                        )}
                                    </div>
                                )}
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No clients found.</p>
                )}
            </div>

            {/* Secțiunea 2: Adăugare Utilizator */}
            <div className="section">
                <h3>Add User</h3>
                <form onSubmit={handleAddUser}>
                    <input
                        type="text"
                        placeholder="Name"
                        value={newUser.name}
                        onChange={(e) => setNewUser({...newUser, name: e.target.value})}
                        required
                    />
                    <input
                        type="text"
                        placeholder="Email"
                        value={newUser.email}
                        onChange={(e) => setNewUser({...newUser, email: e.target.value})}
                        required
                    />
                    <input
                        type="text"
                        placeholder="Username"
                        value={newUser.username}
                        onChange={(e) => setNewUser({...newUser, username: e.target.value})}
                        required
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={newUser.password}
                        onChange={(e) => setNewUser({...newUser, password: e.target.value})}
                        required
                    />
                    <button type="submit">Add User</button>
                </form>
                {message && <p>{message}</p>}
            </div>
        </div>
    );
};
    export default AdminPage;
