import React, { useEffect, useState } from 'react';
import './ClientStyle.css';
import axios from 'axios';
import SockJS from 'sockjs-client';
import { Stomp } from '@stomp/stompjs';
import { Typography,Button, Card, CardContent, Grid } from '@mui/material';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';
import { useNavigate } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const ClientPage = () => {
    const [devices, setDevices] = useState([]);
    const [selectedDevice, setSelectedDevice] = useState(null);
    const [consumptionData, setConsumptionData] = useState(null);
    const [error, setError] = useState('');
    const userId = localStorage.getItem('userId');
    const navigate = useNavigate();
    const [websocketConnected, setWebsocketConnected] = useState(false);


    const handleSignOut = () => {
        localStorage.removeItem('role');
        localStorage.removeItem('userId');
        navigate('/');
    };

    const fetchDevices = async () => {
        if (!userId) {
            setError('User ID is not available');
            return;
        }
        try {
            const response = await axios.get(`http://localhost:8082/api/user_device/${userId}/owned_devices`);
            setDevices(response.data);
        } catch (err) {
            console.error(err);
            setError('Failed to fetch devices');
        }
    };

    const disconnectFromNotifications = () => {
        if (stompClient && websocketConnected) {
            stompClient.disconnect(() => {
                setWebsocketConnected(false); // Setăm starea la false când conexiunea este închisă
                console.log("Disconnected from WebSocket");
            });
        }
    };

    const fetchConsumptionData = async (device) => {
        // Dacă dispozitivul e deja selectat, atunci închidem graficul și deconectăm WebSocket
        if (selectedDevice?.id === device.id) {
            setSelectedDevice(null);
            setConsumptionData(null);
            disconnectFromNotifications(); // Deconectăm WebSocket
            return;
        }

        try {
            const response = await axios.get(`http://localhost:8086/api/measurements/historical`, {
                params: { deviceId: device.id }
            });
            setSelectedDevice(device);
            setConsumptionData(response.data);
            connectToNotifications(); // Conectăm WebSocket când afișăm graficul
        } catch (err) {
            console.error(err);
            setError('Failed to fetch consumption data');
        }
    };


    const NotificationCard = ({ deviceId, hour, consumption, maxConsumption }) => {
        return (
            <Card sx={{ minWidth: 275, marginBottom: '10px' }}>
                <CardContent>
                    <Typography variant="h5" component="div">
                        ⚠️ ALERT! ⚡
                    </Typography>
                    <Typography sx={{ mb: 1.5 }} color="text.secondary">
                        <strong>Device ID:</strong> {deviceId}
                    </Typography>
                    <Typography sx={{ mb: 1.5 }} color="text.secondary">
                        <strong>Hour:</strong> {hour}
                    </Typography>
                    <Typography sx={{ mb: 1.5 }} color="text.secondary">
                        <strong>Consumption:</strong> {consumption} kWh
                    </Typography>
                    <Typography sx={{ mb: 1.5 }} color="text.secondary">
                        <strong>Max allowed:</strong> {maxConsumption} kWh
                    </Typography>
                    <Button variant="contained" color="error">
                        🚨 Threshold exceeded!
                    </Button>
                </CardContent>
            </Card>
        );
    };



    let socketConnected = false;
    let stompClient = null;

    const connectToNotifications = () => {
        if (websocketConnected) return; // Verificăm dacă conexiunea este deja activă

        const socket = new SockJS('http://localhost:8086/websocket');
        stompClient = Stomp.over(socket);

        const sentNotifications = new Set();  // Keep track of sent notifications
        let notifications = [];  // List to store notifications

        stompClient.connect({}, (frame) => {
            setWebsocketConnected(true); // Setăm starea la true când conexiunea este stabilită
            console.log('Connected: ' + frame);

            stompClient.subscribe('/topic/notifications', (notification) => {
                const message = notification.body;

                // Extract notificationId, deviceId, hour, consumption, and maxConsumption from message
                const notificationIdMatch = message.match(/Notification ID: (\S+)/);
                const deviceIdMatch = message.match(/deviceId: (\d+)/);
                const hourMatch = message.match(/Hour: (\d+)/);
                const consumptionMatch = message.match(/Consumption: (\d+(\.\d+)?)/);
                const maxConsumptionMatch = message.match(/Max allowed: (\d+(\.\d+)?)/);

                if (!notificationIdMatch || !deviceIdMatch || !hourMatch || !consumptionMatch || !maxConsumptionMatch) {
                    console.error("Some required data is missing in the message");
                    return;
                }

                const notificationId = notificationIdMatch[1];
                const deviceId = deviceIdMatch[1];
                const hour = parseInt(hourMatch[1]);  // Ensure it's an integer
                const consumption = parseFloat(consumptionMatch[1]);
                const maxConsumption = parseFloat(maxConsumptionMatch[1]);

                // Check if notification has already been sent
                if (sentNotifications.has(notificationId)) {
                    console.log("Duplicate notification detected: ", notificationId);
                    return;  // Ignore if already sent
                }

                // Add notification to list
                notifications.push({
                    notificationId,
                    deviceId,
                    hour,
                    consumption,
                    maxConsumption
                });

                // Mark notification as sent
                sentNotifications.add(notificationId);

                // Sort notifications by hour (ascending order)
                notifications.sort((a, b) => a.hour - b.hour);

                // Send notifications in order
                notifications.forEach((notification, index) => {
                    // Send notification card
                    toast.info(<NotificationCard
                        deviceId={notification.deviceId}
                        hour={notification.hour}
                        consumption={notification.consumption}
                        maxConsumption={notification.maxConsumption} />, {
                        position: "top-right",
                        autoClose: 3000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                    });

                    // If it's the last notification, clear the array
                    if (index === notifications.length - 1) {
                        // Clear notifications array after sending
                        notifications = [];
                    }
                });
            });
        }, (error) => {
            console.error("WebSocket connection failed:", error);
        });
    };

    useEffect(() => {
        fetchDevices();
        // Nu mai conectăm la WebSocket aici, ci doar când afișăm graficul

        return () => {
            disconnectFromNotifications(); // Deconectăm WebSocket când componenta este demontată
        };
    }, [userId]);

    const renderChart = () => {
        if (!consumptionData || consumptionData.length === 0) return <p>No consumption data available for this device.</p>;

        const scaleFactor = 1; // Factor de scalare
        const hours = Array.from({ length: 24 }, (_, index) => index + 1);
        const values = consumptionData.map(value => value / scaleFactor); // Reducem valorile

        const data = {
            labels: hours,
            datasets: [
                {
                    label: 'Energy Consumption (kWh)',
                    data: values,
                    borderColor: 'rgba(75,192,192,1)',
                    backgroundColor: 'rgba(75,192,192,0.2)',
                    tension: 0.1,
                },
            ],
        };

        const options = {
            responsive: true,
            plugins: {
                legend: {
                    display: true,
                    position: 'top',
                    labels: {
                        font: {
                            size: 14, // Dimensiunea fontului în legendă
                        },
                    },
                },
                tooltip: {
                    callbacks: {
                        label: (tooltipItem) =>
                            `${tooltipItem.raw} kWh`, // Adăugăm unitatea în tooltip
                    },
                },
            },
            scales: {
                x: {
                    title: {
                        display: true,
                        text: 'Hours of the Day', // Titlul pentru axa X
                        font: {
                            size: 16,
                        },
                    },
                },
                y: {
                    title: {
                        display: true,
                        text: 'Energy Consumption (kWh)', // Titlul pentru axa Y
                        font: {
                            size: 16,
                        },
                    },
                },
            },
        };

        return <Line data={data} options={options} />;    };

    return (
        <div className="client-dashboard">
            <h2>Client Dashboard</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <Button variant="contained" color="secondary" onClick={handleSignOut} style={{ marginBottom: '20px' }}>
                Sign Out
            </Button>

            <h3 className="devices-title">Your Devices</h3>
            {devices.length > 0 ? (
                <Grid container spacing={3}>
                    {devices.map((device) => (
                        <Grid item xs={12} sm={6} md={4} key={device.id}>
                            <Card style={{ padding: '10px', textAlign: 'center' }}>
                                <CardContent>
                                    <h4>{device.description}</h4>
                                    <p><strong>Max Consumption:</strong> {device.consumption} kWh</p>
                                    <p><strong>Address:</strong> {device.address} </p>

                                    <Button
                                        variant="contained"
                                        color={selectedDevice?.id === device.id ? 'secondary' : 'primary'}
                                        onClick={() => fetchConsumptionData(device)}
                                    >
                                        {selectedDevice?.id === device.id ? 'Hide Consumption' : 'Show Consumption'}
                                    </Button>
                                </CardContent>
                            </Card>
                        </Grid>
                    ))}
                </Grid>
            ) : (
                <p>No devices found.</p>
            )}

            {selectedDevice && (
                <div style={{ marginTop: '30px' }}>
                    <h3>Consumption Data for: {selectedDevice.description}</h3>
                    {renderChart()}
                </div>
            )}

            <ToastContainer />
        </div>
    );
};

export default ClientPage;
