import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './AdminStyle.css';
import { Button } from '@mui/material';

const AdminPage = () => {
    const navigate = useNavigate();
    const [clients, setClients] = useState([]);
    const [newUser, setNewUser] = useState({ username: '', password: '', role: 'client', name: '', email: '' });
    const [error, setError] = useState('');
    const [expandedClientId, setExpandedClientId] = useState(null);
    const [selectedUserId, setSelectedUserId] = useState(null);
    const [showDeviceForm, setShowDeviceForm] = useState({});
    const [devices, setDevices] = useState({});
    const [message, setMessage] = useState('');

    useEffect(() => {
        const userRole = localStorage.getItem('role');
        if (userRole !== 'admin') {
            alert('You do not have access to this page!');
            navigate('/client'); // Redirecționează clientul la pagina lui
        } else {
            fetchClients();
        }
    }, [navigate]);

    const fetchClients = async () => {
        try {
            const response = await axios.get('http://localhost:8084/api/users'); // API pentru a obține utilizatorii
            setClients(response.data);
        } catch (err) {
            console.error(err);
            setError('Failed to fetch clients');
        }
    };

    const fetchDevices = async (userId) => {
        try {
            const response = await axios.get(`http://localhost:8083/api/user_device/${userId}`); // API pentru a obține dispozitivele utilizatorului
            setDevices(prev => ({ ...prev, [userId]: response.data }));
        } catch (err) {
            console.error(err);
            setError('Failed to fetch devices');
        }
    };

    const toggleExpandClient = (clientId) => {
        setExpandedClientId(expandedClientId === clientId ? null : clientId);
        if (expandedClientId !== clientId) {
            fetchDevices(clientId); // Fetch devices only when expanding
        }
    };

    const toggleDeviceForm = (e, userId) => {
        e.stopPropagation(); // Oprește propagarea evenimentului de click
        setShowDeviceForm((prev) => ({ ...prev, [userId]: !prev[userId] }));
    };

    const handleAddDevice = async (e, userId) => {
        e.preventDefault(); // Previne reîncărcarea paginii
        const newDevice = { /* ...adaugă câmpurile necesare aici... */ };
        try {
            await axios.post('http://localhost:8083/api/user_device/insert', { ...newDevice, userId });
            setMessage('Device added successfully!');
            fetchDevices(userId); // Reîncarcă dispozitivele
        } catch (error) {
            console.error('Error adding device:', error);
            setMessage('Failed to add device. Please try again.');
        }
    };

    const handleDeleteDevice = async (deviceId, userId) => {
        if (window.confirm("Are you sure you want to delete this device?")) {
            try {
                await axios.delete(`http://localhost:8083/api/user_device/device/${deviceId}`);
                setMessage('Device deleted successfully');
                fetchDevices(userId); // Reîncarcă dispozitivele
            } catch (err) {
                console.error(err);
                setMessage('Error deleting device');
            }
        }
    };

    return (
        <div className="admin-container">
            <h2>Admin Dashboard</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <Button variant="contained" color="secondary" onClick={() => { /* Log out */ }} style={{ marginBottom: '20px' }}>
                Sign Out
            </Button>

            <div className="section">
                <h3>Client List</h3>
                {clients.length > 0 ? (
                    <ul>
                        {clients.map(client => (
                            <li key={client.id} style={{ cursor: 'pointer' }}>
                                <div onClick={() => toggleExpandClient(client.id)}>
                                    {client.name}
                                </div>

                                {expandedClientId === client.id && (
                                    <div className="client-details">
                                        {/* Client Details */}
                                        <button onClick={(e) => toggleDeviceForm(e, client.id)}>
                                            {showDeviceForm[client.id] ? 'Hide Device Form' : 'Add Device'}
                                        </button>

                                        {/* Dispozitivele utilizatorului */}
                                        {showDeviceForm[client.id] && devices[client.id]?.map(device => (
                                            <div key={device.id} className="device-card">
                                                <p>{device.description} - {device.address}</p>
                                                <button onClick={() => handleDeleteDevice(device.id, client.id)}>Delete Device</button>
                                                {/* Adaugă un formular de actualizare pentru dispozitive aici, dacă este necesar */}
                                            </div>
                                        ))}

                                        {/* Formular pentru adăugarea unui dispozitiv */}
                                        {showDeviceForm[client.id] && (
                                            <form onSubmit={(e) => handleAddDevice(e, client.id)}>
                                                <input type="text" placeholder="Address" required />
                                                <input type="number" placeholder="Consumption" required />
                                                <input type="text" placeholder="Description" required />
                                                <button type="submit">Add Device</button>
                                            </form>
                                        )}
                                    </div>
                                )}
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No clients found.</p>
                )}
            </div>
        </div>
    );
};

export default AdminPage;
