import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();  // Folosește useNavigate pentru redirecționare

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('http://localhost:8084/api/users/login', {
                username,
                password
            });

            console.log(response);  // Verifică răspunsul din backend

            const userRole = response.data.role;
            const userId=response.data.id;
            console.log(response.data);

            localStorage.setItem('role', userRole);
            localStorage.setItem('id',userId);

            if (userRole === 'admin') {
                navigate('/admin');  // Folosește navigate în loc de window.location.href
            } else if (userRole === 'client') {
                navigate('/client');
            }
        } catch (error) {
            console.log(error);
            setUsername('');
            setPassword('');
            setError('Invalid username or password');
        }
    };

    return (
        <div>
            <h2>Login</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Username:</label>
                    <input
                        type="text"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Password:</label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                {error && <p style={{ color: 'red' }}>{error}</p>}
                <button type="submit">Login</button>
            </form>
        </div>
    );
};

export default Login;
