import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './AdminStyle.css';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button, TextField } from '@mui/material';
const AdminPage = () => {
    const navigate = useNavigate();
    const [clients, setClients] = useState([]);
    const [newUser, setNewUser] = useState({ username: '', password: '', role: 'client', name: '', email: '' });
    const [error, setError] = useState('');
    const [expandedClientId, setExpandedClientId] = useState(null);
    const [selectedUserId, setSelectedUserId] = useState(null);
    const [showDeviceForm, setShowDeviceForm] = useState({});
    const [message, setMessage] = useState('');
    const [updatedUser, setUpdatedUser] = useState({ name: '', email: '', username: '', role: '', password: '' });
    const [showUpdateForm, setShowUpdateForm] = useState({});
    const [newDevice, setNewDevice] = useState({ address: '', consumption: '', description: '' });
    const [devices, setDevices] = useState([]); // Dispozitivele unui client selectat
    const [expandedDeviceId, setExpandedDeviceId] = useState(null);
    const userId = localStorage.getItem('userId');
    const [updateForm, setUpdateForm] = useState({ description: '', address: '', consumption: '' });


    useEffect(() => {
        const userRole = localStorage.getItem('role');
        if (userRole !== 'admin') {
            alert('You do not have access to this page!');
            navigate('/client'); // Redirecționează clientul la pagina lui
        }else{
            fetchClients();
        }
    }, [navigate]);

    const toggleExpandClient = (clientId) => {
        setExpandedClientId(expandedClientId === clientId ? null : clientId);
    };

    const handleViewDevices = async (clientId) => {
        try {
            const response = await axios.get(`http://localhost:8083/api/user_device/${clientId}/owned_devices`);
            setDevices(response.data);
        } catch (err) {
            console.error(err);
            alert('Failed to fetch devices for client');
        }
    };
    const handleUpdateDevice = async (deviceId) => {
        try {
            await axios.put(`http://localhost:8083/api/user_device/${deviceId}`, updateForm);
            alert('Device updated successfully');
            setExpandedDeviceId(null);
            handleViewDevices(userId); // Reîncarcă lista dispozitivelor după update
        } catch (err) {
            console.error(err);
            alert('Error updating device');
        }
    };
    const handleDeleteDevice = async (deviceId) => {
        try {
            await axios.delete(`http://localhost:8083/api/user_device/device/${deviceId}`);
            alert('Device deleted successfully');
            setDevices(devices.filter(device => device.id !== deviceId));
        } catch (err) {
            console.error(err);
            alert('Error deleting device');
        }
    };

    // Funcția pentru Sign Out
    const handleSignOut = () => {
        localStorage.removeItem('role');
        localStorage.removeItem('userId');
        navigate('/'); // Redirecționează către pagina de login
    }
    const toggleUpdateForm = (e, userId, client) => {
        e.stopPropagation();
        setSelectedUserId(userId); // setează utilizatorul selectat

        if (!client) {
            console.error("Client is undefined");
            return; // oprește execuția dacă client este undefined
        }

        if (showUpdateForm[userId]) {
            // Dacă formularul este ascuns, resetează câmpurile
            setUpdatedUser({
                name: '',
                email: '',
                username: '',
                password: '',
                role: 'client',
            });
        } else {
            // Când formularul de actualizare este deschis, inițializează-l cu datele utilizatorului
            setUpdatedUser({
                name: client.name || '',
                email: client.email || '',
                username: client.username || '',
                role: client.role || 'client',
            });
        }

        setShowUpdateForm((prevState) => ({
            ...prevState,
            [userId]: !prevState[userId], // toggle pentru formularul utilizatorului curent
        }));
    };


    // Funcția pentru a crea un dispozitiv
    const handleAddDevice = async (e, userId) => {
        e.preventDefault(); // Previne reîncărcarea paginii
        try {
            const response = await axios.post('http://localhost:8083/api/user_device/insert', {
                ...newDevice,
                userId: userId // Aici folosești userId-ul clientului selectat
            });
            console.log(response.data); // Afișează mesajul de succes

            // Setează mesajul de succes
            setMessage('Device added successfully!');

            // Resetează formularul
            setNewDevice({ address: '', consumption: '', description: '' });

            // Oprește afișarea mesajului după câteva secunde
            setTimeout(() => {
                setMessage('');
            }, 3000); // După 3 secunde
        } catch (error) {
            console.error('Error adding device:', error);

            // Setează mesajul de eroare
            setMessage('Failed to add device. Please try again.');

            // Oprește afișarea mesajului după câteva secunde
            setTimeout(() => {
                setMessage('');
            }, 3000); // După 3 secunde
        }
    };

    const handleUpdateUser = async (e) => {
        e.preventDefault();

        // Loghează datele utilizatorului care urmează să fie actualizat
        console.log("Updating user with data:", updatedUser);

        const updatedUserData = {
            ...updatedUser,
            password: updatedUser.password || '',
        };

        try {
            const response = await fetch(`http://localhost:8084/api/users/${selectedUserId}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(updatedUserData),
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || 'Failed to update user');
            }

            // Resetează formularul după actualizare
            setUpdatedUser({ name: '', email: '', username: '', password: '', role: 'client' });

            const responseData = await response.json();
            console.log(responseData);
            setMessage('User updated successfully!');
            setTimeout(() => {
                setMessage('');
            }, 3000);

            fetchClients();
        } catch (error) {
            console.error('Error updating user:', error);
            setMessage(`Failed to update user: ${error.message}`);
            setTimeout(() => {
                setMessage('');
            }, 3000);
        }
    };



    const handleUserSelect = (userId) => {
        setSelectedUserId(userId); // Salvează ID-ul utilizatorului selectat
    };

    const handleDeleteUser = async (id) => {
        if (window.confirm("Are you sure you want to delete this user?")) {
            try {
                await axios.delete(`http://localhost:8084/api/users/${id}`);
                fetchClients(); // Reîncarcă lista de utilizatori
            } catch (error) {
                console.error('Error deleting user:', error);
                setError('Failed to delete user');
            }
        }
    };

    const fetchClients = async () => {
        try {
            const response = await axios.get('http://localhost:8084/api/users'); // API pentru a obține utilizatorii
            setClients(response.data);
        } catch (err) {
            console.error(err);
            setError('Failed to fetch clients');
        }
    };
    const handleAddUser = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://localhost:8084/api/users/insert', newUser); // API pentru a adăuga utilizatori
            setNewUser({
                name:'',
                email:'',
                username:'',
                password:'',
                role:'client'
            });
            fetchClients(); // Reîncarcă lista după adăugare
            setNewUser({ name:'',email:'',username: '', password: '', role: 'client' }); // Resetează formularul
        } catch (err) {
            console.error(err);
            setError('Failed to add user');
        }
    };

    const handleExpandClick = (deviceId) => {
        setExpandedDeviceId(expandedDeviceId === deviceId ? null : deviceId);
    };
    const handleInputChange = (e) => {
        setUpdateForm({ ...updateForm, [e.target.name]: e.target.value });
    };

    const toggleDeviceForm = (e, userId) => {
        e.stopPropagation(); // Oprește propagarea evenimentului de click
        setSelectedUserId(userId); // Setează userId pentru utilizatorul curent
        setShowDeviceForm((prev) => ({ ...prev, [userId]: !prev[userId] }));
    };

    return (
        <div className="client-dashboard">
            <h2>Client Dashboard</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <Button variant="contained" color="secondary" onClick={handleSignOut} style={{ marginBottom: '20px' }}>
                Sign Out
            </Button>
            <h3 className="devices-title">Your Devices</h3>
            {devices.length > 0 ? (
                <TableContainer component={Paper}>
                    <Table>
                        <TableHead>
                            <TableRow>
                                <TableCell>Description</TableCell>
                                <TableCell>Address</TableCell>
                                <TableCell>Consumption</TableCell>
                                <TableCell>Actions</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {devices.map((device) => (
                                <React.Fragment key={device.id}>
                                    <TableRow>
                                        <TableCell>{device.description}</TableCell>
                                        <TableCell>{device.address}</TableCell>
                                        <TableCell>{device.consumption}</TableCell>
                                        <TableCell>
                                            <Button onClick={() => handleExpandClick(device.id)}>
                                                {expandedDeviceId === device.id ? 'Hide' : 'Expand'}
                                            </Button>
                                        </TableCell>
                                    </TableRow>
                                    {expandedDeviceId === device.id && (
                                        <TableRow>
                                            <TableCell colSpan={4}>
                                                <form>
                                                    <TextField
                                                        label="Description"
                                                        name="description"
                                                        value={updateForm.description}
                                                        onChange={handleInputChange}
                                                        fullWidth
                                                        margin="normal"
                                                    />
                                                    <TextField
                                                        label="Address"
                                                        name="address"
                                                        value={updateForm.address}
                                                        onChange={handleInputChange}
                                                        fullWidth
                                                        margin="normal"
                                                    />
                                                    <TextField
                                                        label="Consumption"
                                                        name="consumption"
                                                        value={updateForm.consumption}
                                                        onChange={handleInputChange}
                                                        fullWidth
                                                        margin="normal"
                                                    />
                                                    <Button
                                                        variant="contained"
                                                        color="primary"
                                                        onClick={() => handleUpdateDevice(device.id)}
                                                    >
                                                        Update
                                                    </Button>
                                                    <Button
                                                        variant="contained"
                                                        color="secondary"
                                                        onClick={() => handleDeleteDevice(device.id)}
                                                        style={{ marginLeft: '10px' }}
                                                    >
                                                        Delete
                                                    </Button>
                                                </form>
                                            </TableCell>
                                        </TableRow>
                                    )}
                                </React.Fragment>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
            ) : (
                <p>No devices found.</p>
            )}
        </div>
    );
};

export default AdminPage;
