import React, { useEffect, useState } from 'react';
import './ClientStyle.css';
import axios from 'axios';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button, TextField } from '@mui/material';

const ClientPage = () => {
    const [devices, setDevices] = useState([]);
    const [error, setError] = useState('');
    const userId = localStorage.getItem('userId'); // Obține ID-ul utilizatorului
    const [expandedDevice, setExpandedDevice] = useState(null); // Stare pentru dispozitivul extins

    useEffect(() => {
        const fetchDevices = async () => {
            if (!userId) {
                setError('User ID is not available'); // Mesaj de eroare dacă ID-ul utilizatorului nu este disponibil
                return;
            }
            try {
                const response = await axios.get(`http://localhost:8083/api/user_device/${userId}/owned_devices`);
                setDevices(response.data); // Setează lista de dispozitive în stare
                console.log(response.data);  // Adaugă acest log pentru a vedea răspunsul de login

            } catch (err) {
                console.error(err);
                setError('Failed to fetch devices');
            }
        };

        fetchDevices();
    }, [userId]); // Adaugă userId ca dependență

    const handleDeleteDevice = async (deviceId) => {
        try {
            await axios.delete(`http://localhost:8083/api/user_device/device/${deviceId}`);
            setDevices(devices.filter(device => device.id !== deviceId)); // Elimină dispozitivul din stare
            setExpandedDevice(null); // Închide secțiunea extinsă
        } catch (err) {
            console.error(err);
            setError('Failed to delete device');
        }
    };

    const toggleExpand = (deviceId) => {
        setExpandedDevice(expandedDevice === deviceId ? null : deviceId); // Extinde/colapsează dispozitivul
    };

    return (
        <div className="client-dashboard">
            <h2>Client Dashboard</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <h3 className="devices-title">Your Devices</h3>
            {devices.length > 0 ? (
                <TableContainer component={Paper}>
                    <Table>
                        <TableHead>
                            <TableRow>
                                <TableCell>Description</TableCell>
                                <TableCell>Address</TableCell>
                                <TableCell>Consumption</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {devices.map(device => (
                                <React.Fragment key={device.id}>
                                    <TableRow onClick={() => toggleExpand(device.id)} style={{ cursor: 'pointer' }}>
                                        <TableCell>{device.description}</TableCell>
                                        <TableCell>{device.address}</TableCell>
                                        <TableCell>{device.consumption}</TableCell>
                                    </TableRow>
                                    {expandedDevice === device.id && (
                                        <TableRow>
                                            <TableCell colSpan={3}>
                                                <div className="expanded-device-details">
                                                    <p><strong>Device ID:</strong> {device.id}</p>
                                                    <Button
                                                        variant="contained"
                                                        color="secondary"
                                                        onClick={() => handleDeleteDevice(device.id)}
                                                    >
                                                        Delete Device
                                                    </Button>
                                                </div>
                                            </TableCell>
                                        </TableRow>
                                    )}
                                </React.Fragment>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
            ) : (
                <p>No devices found.</p>
            )}
        </div>
    );
};

export default ClientPage;
