import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './AdminStyle.css';
const AdminPage = () => {
    const navigate = useNavigate();
    const [clients, setClients] = useState([]);
    const [newUser, setNewUser] = useState({ username: '', password: '', role: 'client' });
    const [error, setError] = useState('');
    const [expandedClientId, setExpandedClientId] = useState(null);
    const [selectedUserId, setSelectedUserId] = useState(null);
    const [newDevice, setNewDevice] = useState({
        address: '',
        consumption: '',
        description: '',
    });
    const [showDeviceForm, setShowDeviceForm] = useState({}); // Obiect pentru a controla starea formularelor de dispozitive
    const [message, setMessage] = useState(''); // Pentru a afișa mesajul de succes sau eroare


    useEffect(() => {
        const userRole = localStorage.getItem('role');
        if (userRole !== 'admin') {
            alert('You do not have access to this page!');
            navigate('/client'); // Redirecționează clientul la pagina lui
        }else{
            fetchClients();
        }
    }, [navigate]);

    const toggleExpandClient = (clientId) => {
        setExpandedClientId(expandedClientId === clientId ? null : clientId);
    };

    // Funcția pentru a crea un dispozitiv
    const handleAddDevice = async (e, userId) => {
        e.preventDefault(); // Previne reîncărcarea paginii
        try {
            const response = await axios.post('http://localhost:8083/api/user_device/insert', {
                ...newDevice,
                userId: userId // Aici folosești userId-ul clientului selectat
            });
            console.log(response.data); // Afișează mesajul de succes
            // Resetează formularul
            setNewDevice({ address: '', consumption: '', description: '' });
        } catch (error) {
            console.error('Error adding device:', error);
        }
    };

    const handleUserSelect = (userId) => {
        setSelectedUserId(userId); // Salvează ID-ul utilizatorului selectat
    };

    const handleDeleteUser = async (id) => {
        if (window.confirm("Are you sure you want to delete this user?")) {
            try {
                await axios.delete(`http://localhost:8084/api/users/${id}`);
                fetchClients(); // Reîncarcă lista de utilizatori
            } catch (error) {
                console.error('Error deleting user:', error);
                setError('Failed to delete user');
            }
        }
    };

    const fetchClients = async () => {
        try {
            const response = await axios.get('http://localhost:8084/api/users'); // API pentru a obține utilizatorii
            setClients(response.data);
        } catch (err) {
            console.error(err);
            setError('Failed to fetch clients');
        }
    };
    const handleAddUser = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://localhost:8084/api/users/insert', newUser); // API pentru a adăuga utilizatori
            setNewUser({
                name:'',
                email:'',
                username:'',
                password:'',
                role:'client'
            });
            fetchClients(); // Reîncarcă lista după adăugare
            setNewUser({ name:'',email:'',username: '', password: '', role: 'client' }); // Resetează formularul
        } catch (err) {
            console.error(err);
            setError('Failed to add user');
        }
    };


    const toggleDeviceForm = (e, userId) => {
        e.stopPropagation(); // Oprește propagarea evenimentului de click
        setSelectedUserId(userId); // Setează userId pentru utilizatorul curent
        setShowDeviceForm((prev) => ({ ...prev, [userId]: !prev[userId] }));
    };
    return (
        <div className="admin-container">
            <h2>Admin Dashboard</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}

            {/* Secțiunea 1: Lista Clienților */}
            <div className="section">
                <h3>Client List</h3>
                {clients.length > 0 ? (
                    <ul>
                        {clients.map(client => (
                            <li key={client.id} style={{ cursor: 'pointer' }}>
                                {/* Doar titlul utilizatorului este clickabil pentru expand/collapse */}
                                <div onClick={() => toggleExpandClient(client.id)}>
                                    {client.name}
                                </div>

                                {expandedClientId === client.id && (
                                    <div className="client-details">
                                        <p>Id: {client.id}</p>
                                        <p>Username: {client.username}</p>
                                        <p>Email: {client.email}</p>
                                        <button
                                            onClick={(e) => {
                                                e.stopPropagation();
                                                handleDeleteUser(client.id);
                                            }}
                                            className="delete-button"
                                        >
                                            Delete
                                        </button>

                                        {/* Buton pentru a adăuga un dispozitiv */}
                                        <button onClick={(e) => toggleDeviceForm(e, client.id)}>
                                            {showDeviceForm[client.id] ? 'Hide Device Form' : 'Add Device'}
                                        </button>

                                        {/* Formular pentru adăugarea unui dispozitiv */}
                                        {showDeviceForm[client.id] && (
                                            <form onSubmit={(e) => handleAddDevice(e, client.id)}>
                                                <input
                                                    type="text"
                                                    placeholder="Address"
                                                    value={newDevice.address}
                                                    onChange={(e) => setNewDevice({ ...newDevice, address: e.target.value })}
                                                    required
                                                />
                                                <input
                                                    type="number"
                                                    placeholder="Consumption"
                                                    value={newDevice.consumption}
                                                    onChange={(e) => setNewDevice({ ...newDevice, consumption: e.target.value })}
                                                    required
                                                />
                                                <input
                                                    type="text"
                                                    placeholder="Description"
                                                    value={newDevice.description}
                                                    onChange={(e) => setNewDevice({ ...newDevice, description: e.target.value })}
                                                    required
                                                />
                                                <button type="submit">Add Device</button>
                                            </form>
                                        )}
                                    </div>
                                )}
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No clients found.</p>
                )}
            </div>

            {/* Secțiunea 2: Adăugare Utilizator */}
            <div className="section">
                <h3>Add User</h3>
                <form onSubmit={handleAddUser}>
                    <input
                        type="text"
                        placeholder="Name"
                        value={newUser.name}
                        onChange={(e) => setNewUser({...newUser, name: e.target.value})}
                        required
                    />
                    <input
                        type="text"
                        placeholder="Email"
                        value={newUser.email}
                        onChange={(e) => setNewUser({...newUser, email: e.target.value})}
                        required
                    />
                    <input
                        type="text"
                        placeholder="Username"
                        value={newUser.username}
                        onChange={(e) => setNewUser({...newUser, username: e.target.value})}
                        required
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={newUser.password}
                        onChange={(e) => setNewUser({...newUser, password: e.target.value})}
                        required
                    />
                    <select
                        value={newUser.role}
                        onChange={(e) => setNewUser({...newUser, role: e.target.value})}
                    >
                        <option value="client">Client</option>
                        <option value="admin">Admin</option>
                    </select>
                    <button type="submit">Add User</button>
                </form>
            </div>


        </div>
    );
};

export default AdminPage;
