import React, { useEffect, useState } from 'react';
import './ClientStyle.css';
import axios from 'axios';
import SockJS from 'sockjs-client';
import { Stomp } from '@stomp/stompjs';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify'; // Import Toast components
import 'react-toastify/dist/ReactToastify.css'; // Import Toast CSS

const ClientPage = () => {
    const [devices, setDevices] = useState([]);
    const [error, setError] = useState('');
    const userId = localStorage.getItem('userId');
    const navigate = useNavigate();

    const handleSignOut = () => {
        localStorage.removeItem('role');
        localStorage.removeItem('userId');
        navigate('/');
    };

    const fetchDevices = async () => {
        if (!userId) {
            setError('User ID is not available');
            return;
        }
        try {
            const response = await axios.get(`http://localhost:8083/api/user_device/${userId}/owned_devices`);
            setDevices(response.data);
        } catch (err) {
            console.error(err);
            setError('Failed to fetch devices');
        }
    };

    // Funcția pentru a inițializa conexiunea WebSocket și a asculta alarmele
    const connectToNotifications = () => {
        const socket = new SockJS('http://localhost:8086/websocket');
        const stompClient = Stomp.over(socket);

        stompClient.connect({}, (frame) => {
            console.log('Connected: ' + frame);

            stompClient.subscribe('/topic/notifications', (notification) => {
                const message = notification.body;
                // Display toast notification
                toast.info(message, {
                    position: "top-right",
                    autoClose: 3000, // Duration in milliseconds (3 seconds)
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                });
            });
        }, (error) => {
            console.error("WebSocket connection failed:", error);
        });
    };

    useEffect(() => {
        fetchDevices();
        connectToNotifications();
    }, [userId]);

    return (
        <div className="client-dashboard">
            <h2>Client Dashboard</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <Button variant="contained" color="secondary" onClick={handleSignOut} style={{ marginBottom: '20px' }}>
                Sign Out
            </Button>

            <h3 className="devices-title">Your Devices</h3>
            {devices.length > 0 ? (
                <TableContainer component={Paper}>
                    <Table>
                        <TableHead>
                            <TableRow>
                                <TableCell>Description</TableCell>
                                <TableCell>Address</TableCell>
                                <TableCell>Consumption</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {devices.map((device) => (
                                <TableRow key={device.id}>
                                    <TableCell>{device.description}</TableCell>
                                    <TableCell>{device.address}</TableCell>
                                    <TableCell>{device.consumption}</TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
            ) : (
                <p>No devices found.</p>
            )}

            {/* Toast Container to display notifications */}
            <ToastContainer />
        </div>
    );
};

export default ClientPage;
