import React, { useState } from 'react';
import axios from 'axios';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('http://localhost:8084/api/users/login', {
                username,
                password
            });

            console.log(response);  // Verifică răspunsul din backend

            const userRole = response.data.role;  // Presupunem că serverul trimite și rolul
            localStorage.setItem('role', userRole);

            if (userRole === 'admin') {
                window.location.href = '/admin';
            } else if (userRole === 'client') {
                window.location.href = '/client';
            }
        } catch (error) {
            console.log(error);  // Verifică dacă apare o eroare
            setError('Invalid username or password');
        }
    };

    return (
        <div>
            <h2>Login</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Username:</label>
                    <input
                        type="text"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Password:</label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                {error && <p style={{ color: 'red' }}>{error}</p>}
                <button type="submit">Login</button>
            </form>
        </div>
    );
};

export default Login;
