import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './LoginStyle.css';
import { TextField, Button, Paper } from '@mui/material';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();  // Folosește useNavigate pentru redirecționare

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('http://localhost:8084/api/users/login', {
                username,
                password
            });

            console.log(response);  // Verifică răspunsul din backend

            const userRole = response.data.role;
            const userId = response.data.id; // Presupunând că ID-ul utilizatorului este inclus în răspuns
            localStorage.setItem('role', userRole);
            localStorage.setItem('userId', userId); // Salvează ID-ul utilizatorului

            if (userRole === 'admin') {
                navigate('/admin');  // Folosește navigate în loc de window.location.href
            } else if (userRole === 'client') {
                navigate('/client');
            }
        } catch (error) {
            console.log(error);
            setError('Invalid username or password');
        }
    };
    return (
        <div className="login-container">
            <Paper className="login-form">
                <h2>Welcome!</h2>
                <h3>Sign In</h3>
                <form onSubmit={handleSubmit}>
                    <TextField
                        label="Username"
                        variant="outlined"
                        fullWidth
                        margin="normal"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                    />
                    <TextField
                        label="Password"
                        type="password"
                        variant="outlined"
                        fullWidth
                        margin="normal"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                    {error && <p style={{ color: 'red' }}>{error}</p>}
                    <Button type="submit" variant="contained" color="primary" fullWidth>
                        Login
                    </Button>
                </form>
            </Paper>
        </div>
    );
};

export default Login;
