import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './AdminStyle.css';
import { Button } from "@mui/material";

const AdminPage = () => {
    const navigate = useNavigate();
    const [clients, setClients] = useState([]);
    const [expandedClientId, setExpandedClientId] = useState(null);
    const [showDeviceForm, setShowDeviceForm] = useState({});
    const [message, setMessage] = useState('');
    const [devices, setDevices] = useState([]); // Stare nouă pentru dispozitivele unui utilizator

    useEffect(() => {
        const userRole = localStorage.getItem('role');
        if (userRole !== 'admin') {
            alert('You do not have access to this page!');
            navigate('/client');
        } else {
            fetchClients();
        }
    }, [navigate]);

    const toggleExpandClient = (clientId) => {
        if (expandedClientId === clientId) {
            setExpandedClientId(null);
        } else {
            setExpandedClientId(clientId);
            fetchDevicesForClient(clientId);
        }
    };

    // Funcția pentru a obține lista dispozitivelor unui utilizator
    const fetchDevices = async (userId) => {
        try {
            const response = await axios.get(`http://localhost:8083/api/user_device/${userId}/owned_devices`);
            setDevices(response.data); // Salvează dispozitivele în stare
        } catch (error) {
            console.error("Error fetching devices:", error);
            setMessage('Failed to load devices.');
        }
    };

    const fetchClients = async () => {
        try {
            const response = await axios.get('http://localhost:8084/api/users');
            setClients(response.data);
        } catch (err) {
            console.error(err);
            setMessage('Failed to fetch clients');
        }
    };

    return (
        <div className="admin-container">
            <h2>Admin Dashboard</h2>
            <Button variant="contained" color="secondary" onClick={() => navigate('/')} style={{ marginBottom: '20px' }}>
                Sign Out
            </Button>
            <div className="section">
                <h3>Client List</h3>
                {clients.length > 0 ? (
                    <ul>
                        {clients.map(client => (
                            <li key={client.id} style={{ cursor: 'pointer' }}>
                                <div onClick={() => toggleExpandClient(client.id)}>
                                    {client.name}
                                </div>
                                {expandedClientId === client.id && (
                                    <div className="client-details">
                                        <div className="client-card">
                                            <p>Id: {client.id}</p>
                                            <p>Username: {client.username}</p>
                                            <p>Email: {client.email}</p>
                                            {/* Buton View Device */}
                                            <button onClick={() => fetchDevices(client.id)}>View Devices</button>
                                            {devices.length > 0 ? (
                                                <div className="device-list">
                                                    <h4>Devices:</h4>
                                                    <ul>
                                                        {devices.map(device => (
                                                            <li key={device.id}>
                                                                <p>Address: {device.address}</p>
                                                                <p>Consumption: {device.consumption}</p>
                                                                <p>Description: {device.description}</p>
                                                            </li>
                                                        ))}
                                                    </ul>
                                                </div>
                                            ) : (
                                                <p>No devices found for this client.</p>
                                            )}
                                        </div>
                                    </div>
                                )}
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No clients found.</p>
                )}
            </div>
        </div>
    );
};

export default AdminPage;
