import React, { useEffect, useState } from 'react';
import './ClientStyle.css';
import axios from 'axios';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button, TextField } from '@mui/material';

const ClientPage = () => {
    const [devices, setDevices] = useState([]);
    const [expandedDeviceId, setExpandedDeviceId] = useState(null); // Stochează ID-ul dispozitivului expandat
    const [updateForm, setUpdateForm] = useState({ description: '', address: '', consumption: '' });
    const [error, setError] = useState('');
    const userId = localStorage.getItem('userId'); // Obține ID-ul utilizatorului

    useEffect(() => {
        const fetchDevices = async () => {
            if (!userId) {
                setError('User ID is not available'); // Mesaj de eroare dacă ID-ul utilizatorului nu este disponibil
                return;
            }
            try {
                const response = await axios.get(`http://localhost:8083/api/user_device/${userId}/owned_devices`);
                setDevices(response.data); // Setează lista de dispozitive în stare
            } catch (err) {
                console.error(err);
                setError('Failed to fetch devices');
            }
        };

        fetchDevices();
    }, [userId]);

    const handleExpandClick = (deviceId) => {
        setExpandedDeviceId(expandedDeviceId === deviceId ? null : deviceId); // Expandează sau restrânge
    };

    const handleInputChange = (e) => {
        setUpdateForm({ ...updateForm, [e.target.name]: e.target.value });
    };

    const handleUpdateDevice = async (deviceId) => {
        try {
            await axios.put(`http://localhost:8083/api/user_device/${deviceId}`, updateForm);
            alert('Device updated successfully');
            setExpandedDeviceId(null); // Închide formularul după actualizare
        } catch (err) {
            console.error(err);
            alert('Error updating device');
        }
    };

    const handleDeleteDevice = async (deviceId) => {
        try {
            await axios.delete(`http://localhost:8083/api/user_device/${deviceId}`);
            alert('Device deleted successfully');
            setDevices(devices.filter(device => device.id !== deviceId)); // Elimină dispozitivul șters din listă
        } catch (err) {
            console.error(err);
            alert('Error deleting device');
        }
    };

    return (
        <div className="client-dashboard">
            <h2>Client Dashboard</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <h3 className="devices-title">Your Devices</h3>
            {devices.length > 0 ? (
                <TableContainer component={Paper}>
                    <Table>
                        <TableHead>
                            <TableRow>
                                <TableCell>Description</TableCell>
                                <TableCell>Address</TableCell>
                                <TableCell>Consumption</TableCell>
                                <TableCell>Actions</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {devices.map((device) => (
                                <React.Fragment key={device.id}>
                                    <TableRow>
                                        <TableCell>{device.description}</TableCell>
                                        <TableCell>{device.address}</TableCell>
                                        <TableCell>{device.consumption}</TableCell>
                                        <TableCell>
                                            <Button onClick={() => handleExpandClick(device.id)}>
                                                {expandedDeviceId === device.id ? 'Hide' : 'Expand'}
                                            </Button>
                                        </TableCell>
                                    </TableRow>
                                    {expandedDeviceId === device.id && (
                                        <TableRow>
                                            <TableCell colSpan={4}>
                                                <form>
                                                    <TextField
                                                        label="Description"
                                                        name="description"
                                                        value={updateForm.description}
                                                        onChange={handleInputChange}
                                                        fullWidth
                                                        margin="normal"
                                                    />
                                                    <TextField
                                                        label="Address"
                                                        name="address"
                                                        value={updateForm.address}
                                                        onChange={handleInputChange}
                                                        fullWidth
                                                        margin="normal"
                                                    />
                                                    <TextField
                                                        label="Consumption"
                                                        name="consumption"
                                                        value={updateForm.consumption}
                                                        onChange={handleInputChange}
                                                        fullWidth
                                                        margin="normal"
                                                    />
                                                    <Button
                                                        variant="contained"
                                                        color="primary"
                                                        onClick={() => handleUpdateDevice(device.id)}
                                                    >
                                                        Update Device
                                                    </Button>
                                                    <Button
                                                        variant="contained"
                                                        color="secondary"
                                                        onClick={() => handleDeleteDevice(device.id)}
                                                        style={{ marginLeft: '10px' }}
                                                    >
                                                        Delete Device
                                                    </Button>
                                                </form>
                                            </TableCell>
                                        </TableRow>
                                    )}
                                </React.Fragment>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
            ) : (
                <p>No devices found.</p>
            )}
        </div>
    );
};

export default ClientPage;
