import React, { useEffect, useState } from 'react';
import './ClientStyle.css';
import axios from 'axios';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button, TextField } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const ClientPage = () => {
    const [devices, setDevices] = useState([]);
    const [expandedDeviceId, setExpandedDeviceId] = useState(null);
    const [updateForm, setUpdateForm] = useState({ description: '', address: '', consumption: '' });
    const [error, setError] = useState('');
    const userId = localStorage.getItem('userId');
    const navigate = useNavigate();  // Folosește useNavigate pentru redirecționare

    // Funcția pentru Sign Out
    const handleSignOut = () => {
        localStorage.removeItem('role');
        localStorage.removeItem('userId');
        navigate('/'); // Redirecționează către pagina de login
    }
    const fetchDevices = async () => {
        if (!userId) {
            setError('User ID is not available');
            return;
        }
        try {
            const response = await axios.get(`http://localhost:8083/api/user_device/${userId}/owned_devices`);
            setDevices(response.data);
        } catch (err) {
            console.error(err);
            setError('Failed to fetch devices');
        }
    };

    const handleViewDevices = async (clientId) => {
        try {
            const response = await axios.get(`http://localhost:8083/api/user_device/${clientId}/owned_devices`);
            setDevices(response.data);
        } catch (err) {
            console.error(err);
            alert('Failed to fetch devices for client');
        }
    };

    const handleExpandClick = (deviceId) => {
        setExpandedDeviceId(expandedDeviceId === deviceId ? null : deviceId);
    };
    const handleUpdateDevice = async (deviceId) => {
        try {
            await axios.put(`http://localhost:8083/api/user_device/${deviceId}`, updateForm);
            alert('Device updated successfully');
            setExpandedDeviceId(null);
            handleViewDevices(userId); // Reîncarcă lista dispozitivelor după update
        } catch (err) {
            console.error(err);
            alert('Error updating device');
        }
    };
    useEffect(() => {
        fetchDevices();
    }, [userId]);


    const handleInputChange = (e) => {
        setUpdateForm({ ...updateForm, [e.target.name]: e.target.value });
    };


    const handleDeleteDevice = async (deviceId) => {
        try {
            await axios.delete(`http://localhost:8083/api/user_device/device/${deviceId}`);
            alert('Device deleted successfully');
            setDevices(devices.filter(device => device.id !== deviceId));
        } catch (err) {
            console.error(err);
            alert('Error deleting device');
        }
    };

    return (
        <div className="client-dashboard">
            <h2>Client Dashboard</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <Button variant="contained" color="secondary" onClick={handleSignOut} style={{ marginBottom: '20px' }}>
                Sign Out
            </Button>
            <h3 className="clients-title">Clients</h3>
            {clients.length > 0 ? (
                <TableContainer component={Paper}>
                    <Table>
                        <TableHead>
                            <TableRow>
                                <TableCell>Client ID</TableCell>
                                <TableCell>Name</TableCell>
                                <TableCell>Actions</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {clients.map((client) => (
                                <TableRow key={client.id}>
                                    <TableCell>{client.id}</TableCell>
                                    <TableCell>{client.name}</TableCell>
                                    <TableCell>
                                        <Button onClick={() => handleViewDevices(client.id)} variant="contained" color="primary">
                                            View Devices
                                        </Button>
                                    </TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
            ) : (
                <p>No clients found.</p>
            )}

            {devices.length > 0 && (
                <>
                    <h3 className="devices-title">Devices for Selected Client</h3>
                    <TableContainer component={Paper}>
                        <Table>
                            <TableHead>
                                <TableRow>
                                    <TableCell>Description</TableCell>
                                    <TableCell>Address</TableCell>
                                    <TableCell>Consumption</TableCell>
                                    <TableCell>Actions</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {devices.map((device) => (
                                    <React.Fragment key={device.id}>
                                        <TableRow>
                                            <TableCell>{device.description}</TableCell>
                                            <TableCell>{device.address}</TableCell>
                                            <TableCell>{device.consumption}</TableCell>
                                            <TableCell>
                                                <Button onClick={() => handleExpandClick(device.id)}>
                                                    {expandedDeviceId === device.id ? 'Hide' : 'Expand'}
                                                </Button>
                                            </TableCell>
                                        </TableRow>
                                        {expandedDeviceId === device.id && (
                                            <TableRow>
                                                <TableCell colSpan={4}>
                                                    <form>
                                                        <TextField
                                                            label="Description"
                                                            name="description"
                                                            value={updateForm.description}
                                                            onChange={handleInputChange}
                                                            fullWidth
                                                            margin="normal"
                                                        />
                                                        <TextField
                                                            label="Address"
                                                            name="address"
                                                            value={updateForm.address}
                                                            onChange={handleInputChange}
                                                            fullWidth
                                                            margin="normal"
                                                        />
                                                        <TextField
                                                            label="Consumption"
                                                            name="consumption"
                                                            value={updateForm.consumption}
                                                            onChange={handleInputChange}
                                                            fullWidth
                                                            margin="normal"
                                                        />
                                                        <Button
                                                            variant="contained"
                                                            color="primary"
                                                            onClick={() => handleUpdateDevice(device.id)}
                                                        >
                                                            Update
                                                        </Button>
                                                        <Button
                                                            variant="contained"
                                                            color="secondary"
                                                            onClick={() => handleDeleteDevice(device.id)}
                                                            style={{ marginLeft: '10px' }}
                                                        >
                                                            Delete
                                                        </Button>
                                                    </form>
                                                </TableCell>
                                            </TableRow>
                                        )}
                                    </React.Fragment>
                                ))}
                            </TableBody>
                        </Table>
                    </TableContainer>
                </>
            )}
        </div>
    );
};

export default ClientPage;
