import React, { useEffect, useState } from 'react';
import './ClientStyle.css';
import axios from 'axios';
import SockJS from 'sockjs-client';
import { Stomp } from '@stomp/stompjs';
import { Button, Card, CardContent, Grid } from '@mui/material';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';
import { useNavigate } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const ClientPage = () => {
    const [devices, setDevices] = useState([]);
    const [selectedDevice, setSelectedDevice] = useState(null);
    const [consumptionData, setConsumptionData] = useState(null);
    const [error, setError] = useState('');
    const userId = localStorage.getItem('userId');
    const navigate = useNavigate();

    const handleSignOut = () => {
        localStorage.removeItem('role');
        localStorage.removeItem('userId');
        navigate('/');
    };

    const fetchDevices = async () => {
        if (!userId) {
            setError('User ID is not available');
            return;
        }
        try {
            const response = await axios.get(`http://localhost:8082/api/user_device/${userId}/owned_devices`);
            setDevices(response.data);
        } catch (err) {
            console.error(err);
            setError('Failed to fetch devices');
        }
    };

    const fetchConsumptionData = async (device) => {
        // DacÄƒ dispozitivul e deja selectat, atunci Ã®nchidem graficul
        if (selectedDevice?.id === device.id) {
            setSelectedDevice(null);
            setConsumptionData(null);
            return;
        }

        try {
            const response = await axios.get(`http://localhost:8086/api/measurements/historical`, {
                params: { deviceId: device.id }
            });
            setSelectedDevice(device);
            setConsumptionData(response.data);
        } catch (err) {
            console.error(err);
            setError('Failed to fetch consumption data');
        }
    };


    let socketConnected = false;
    let stompClient = null;

    const connectToNotifications = () => {
        if (socketConnected) return;

        const socket = new SockJS('http://localhost:8086/websocket');
        stompClient = Stomp.over(socket);

        const sentNotifications = new Set();
        let notifications = []; // Lista pentru a stoca notificÄƒrile

        stompClient.connect({}, (frame) => {
            socketConnected = true;
            console.log('Connected: ' + frame);

            stompClient.subscribe('/topic/notifications', (notification) => {
                const message = notification.body;

                // Extragem notificationId È™i consumul din mesaj
                const notificationIdMatch = message.match(/Notification ID: (\S+)/);
                if (!notificationIdMatch) {
                    console.error("Notification ID not found in the message");
                    return;
                }
                const notificationId = notificationIdMatch[1];

                // Extragem consumul È™i limita maximÄƒ din mesaj
                const consumptionMatch = message.match(/Consumption: (\d+(\.\d+)?)/);
                const maxConsumptionMatch = message.match(/Max allowed: (\d+(\.\d+)?)/);
                if (!consumptionMatch || !maxConsumptionMatch) {
                    console.error("Consumption or max allowed not found in the message");
                    return;
                }
                const consumption = parseFloat(consumptionMatch[1]);
                const maxConsumption = parseFloat(maxConsumptionMatch[1]);

                // VerificÄƒm dacÄƒ notificarea a fost deja trimisÄƒ
                if (sentNotifications.has(notificationId)) {
                    console.log("Duplicate notification detected: ", notificationId);
                    return; // DacÄƒ notificarea a fost deja trimisÄƒ, o ignorÄƒm
                }

                // AdÄƒugÄƒm notificarea Ã®n lista de notificÄƒri
                notifications.push({
                    message: message,
                    notificationId: notificationId,
                    consumption: consumption,
                    maxConsumption: maxConsumption
                });

                // AdÄƒugÄƒm ID-ul notificÄƒrii Ã®n setul pentru a o marca ca trimisÄƒ
                sentNotifications.add(notificationId);

                // SortÄƒm notificÄƒrile dupÄƒ consum (descrescÄƒtor)
                notifications.sort((a, b) => b.consumption - a.consumption);

                // Trimitem notificÄƒrile ordonate
                notifications.forEach((notification, index) => {
                    // ÃŽmbunÄƒtÄƒÈ›im mesajul de afiÈ™at
                    const formattedMessage = `
                    âš ï¸ **Alert!** âš¡
                    - **Device ID:** ${notification.deviceId}
                    - **Hour:** ${notification.hour}
                    - **Consumption:** ${notification.consumption.toFixed(2)} kWh
                    - **Max allowed:** ${notification.maxConsumption.toFixed(2)} kWh

                    ðŸš¨ **Threshold exceeded!**
                `;

                    // Trimitem notificarea
                    toast.info(formattedMessage, {
                        position: "top-right",
                        autoClose: 3000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                    });

                    // DupÄƒ ce trimitem notificarea, o eliminÄƒm din lista de notificÄƒri
                    if (index === notifications.length - 1) {
                        // Clear the notifications array after sending
                        notifications = [];
                    }
                });
            });
        }, (error) => {
            console.error("WebSocket connection failed:", error);
        });
    };


    useEffect(() => {
        fetchDevices();
        connectToNotifications();
    }, [userId]);

    const renderChart = () => {
        if (!consumptionData || consumptionData.length === 0) return <p>No consumption data available for this device.</p>;

        const scaleFactor = 1; // Factor de scalare
        const hours = Array.from({ length: 24 }, (_, index) => index + 1);
        const values = consumptionData.map(value => value / scaleFactor); // Reducem valorile

        const data = {
            labels: hours,
            datasets: [
                {
                    label: 'Energy Consumption (kWh)',
                    data: values,
                    borderColor: 'rgba(75,192,192,1)',
                    backgroundColor: 'rgba(75,192,192,0.2)',
                    tension: 0.1,
                },
            ],
        };

        const options = {
            responsive: true,
            plugins: {
                legend: {
                    display: true,
                    position: 'top',
                    labels: {
                        font: {
                            size: 14, // Dimensiunea fontului Ã®n legendÄƒ
                        },
                    },
                },
                tooltip: {
                    callbacks: {
                        label: (tooltipItem) =>
                            `${tooltipItem.raw} kWh`, // AdÄƒugÄƒm unitatea Ã®n tooltip
                    },
                },
            },
            scales: {
                x: {
                    title: {
                        display: true,
                        text: 'Hours of the Day', // Titlul pentru axa X
                        font: {
                            size: 16,
                        },
                    },
                },
                y: {
                    title: {
                        display: true,
                        text: 'Energy Consumption (kWh)', // Titlul pentru axa Y
                        font: {
                            size: 16,
                        },
                    },
                },
            },
        };

        return <Line data={data} options={options} />;    };

    return (
        <div className="client-dashboard">
            <h2>Client Dashboard</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <Button variant="contained" color="secondary" onClick={handleSignOut} style={{ marginBottom: '20px' }}>
                Sign Out
            </Button>

            <h3 className="devices-title">Your Devices</h3>
            {devices.length > 0 ? (
                <Grid container spacing={3}>
                    {devices.map((device) => (
                        <Grid item xs={12} sm={6} md={4} key={device.id}>
                            <Card style={{ padding: '10px', textAlign: 'center' }}>
                                <CardContent>
                                    <h4>{device.description}</h4>
                                    <p><strong>Max Consumption:</strong> {device.consumption} kWh</p>
                                    <p><strong>Address:</strong> {device.address} </p>

                                    <Button
                                        variant="contained"
                                        color={selectedDevice?.id === device.id ? 'secondary' : 'primary'}
                                        onClick={() => fetchConsumptionData(device)}
                                    >
                                        {selectedDevice?.id === device.id ? 'Hide Consumption' : 'Show Consumption'}
                                    </Button>
                                </CardContent>
                            </Card>
                        </Grid>
                    ))}
                </Grid>
            ) : (
                <p>No devices found.</p>
            )}

            {selectedDevice && (
                <div style={{ marginTop: '30px' }}>
                    <h3>Consumption Data for: {selectedDevice.description}</h3>
                    {renderChart()}
                </div>
            )}

            <ToastContainer />
        </div>
    );
};

export default ClientPage;
